insert into SOLAR_SYSTEM (name,overview)
values
	('Sun','The Sun is the only star in our solar system. It is the center of our solar system, and its gravity holds the solar system together.'),
	('Moon', 'The Moon is Earth''s only natural satellite. At about one-quarter the diameter of Earth, it is the fifth largest satellite in the Solar System.'),
	('Mercury', 'Mercury is the smallest planet in the Solar System and the closest to the Sun. Its orbit around the Sun takes 87.97 Earth days, the shortest of all the Sun''s planets.'),
	('Venus', 'Venus is the second planet from the Sun and is named after the Roman goddess of love and beauty. As the brightest natural object in Earth''s night sky after the Moon, Venus can cast shadows and can be visible to the naked eye in broad daylight.'),
	('Earth', 'Earth is the third planet from the Sun and the only astronomical object known to harbor life.  About 71% of Earth''s surface is made up of the ocean. The remaining 29% of Earth''s surface is land.'),
	('Mars', 'Mars is the fourth planet from the Sun and the second-smallest planet in the Solar System, being larger than only Mercury. In English, Mars carries the name of the Roman god of war.'),
	('Jupiter', 'Jupiter is the fifth planet from the Sun and the largest in the Solar System. It is a gas giant with a mass more than two and a half times that of all the other planets in the Solar System combined.'),
	('Saturn', 'Saturn is the sixth planet from the Sun and the second-largest in the Solar System, after Jupiter. It is a gas giant with an average radius of about nine and a half times that of Earth.'),
	('Uranus', 'Uranus is the seventh planet from the Sun. It has the third-largest planetary radius and fourth-largest planetary mass in the Solar System.'),
	('Neptune', 'Neptune is the eighth and farthest-known Solar planet from the Sun. In the Solar System, it is the fourth-largest planet by diameter, the third-most-massive planet, and the densest giant plane.'),
	('Pluto', 'Pluto is a dwarf planet in the Kuiper belt, a ring of bodies beyond the orbit of Neptune. It was the first object discovered in the Kuiper belt and remains the largest known body in that area.');